macro_rules! create_icon_functions {
    ($( $icon:ident ),*) => {
        #[allow(unused)]
        use floem::{
            peniko::Color,
            reactive::{create_signal, use_context, ReadSignal, WriteSignal},
            style::*,
            view::View,
            views::*,
        };
        $(
            paste::paste!{
                pub fn [<icon_ $icon:lower>]() -> Svg {
                    svg( || String::from($icon))
                }
            }
        )*
    }
}

